factorial:
func factorial(int x){
    if x == 1{
        return 1;
    } else {
        return x * factorial(x - 1);
    }
}

func factorialFor(int x){
    int result = 1;
    for(int x = x; x > 0; x--){
        result *= x;
    }

    return result;
}

int result = factorial(20);
println(result);

int result2 = factorialFor(20);
println(result2);

